mapper.select:
SELECT p.pkid AS projectId, p.project_name as projectName, p.project_status AS projectStatus, DATE_FORMAT(p.create_date,'%Y-%m-%d') AS createDateStr, p.project_kind AS projectKind, pp.name AS name, pp.sex AS sex, area.area_name AS areaName, town.area_name AS townName, p.refuse_result AS refuseResult FROM project p LEFT JOIN project_person pp ON p.pkid = pp.project_id LEFT JOIN area_code area ON area.code = pp.area_code LEFT JOIN area_code town ON town.code = pp.town_code
AND p.project_status != 0
AND p.create_user = #{pro.createUser}
AND p.project_kind = #{pro.projectKind}
AND p.project_status in (1, 4)
AND pp.info_kind = #{pro.infoKind}
AND pp.town_code = #{pro.townCode}
AND pp.area_code = #{pro.areaCode}
AND p.project_name like CONCAT('%',#{pro.projectName},'%')
ORDER BY p.pkid DESC

select A.pkid AS projectId, A.project_name AS projectName, C.name AS personName, A.project_kind AS projectKind, A.project_status AS projectStatus, B.show_preice AS showPreice, DATE_FORMAT(A.create_date,'%Y-%m-%d') AS createDateStr, D.user_name AS auditUserName, DATE_FORMAT(A.audit_date,'%Y-%m-%d') AS auditDateStr from project A left join project_around B ON A.pkid = B.project_id left join project_person C ON A.pkid = C.project_id left join users D ON A.audit_user = D.pkid
A.project_status NOT IN (0, 5)
AND A.project_name like CONCAT('%',#{projectName},'%')
AND C.name like CONCAT('%',#{personName},'%')
AND A.project_kind = #{projectKind}
AND A.project_status = #{projectStatus}
AND A.create_date >= #{startDate}
AND A.create_date <= #{endDate}
AND C.area_code = #{areaCode}
AND C.town_code = #{townCode}
AND A.project_status IN (6, 7, 8)
ORDER BY A.pkid DESC, A.project_status DESC

SELECT ALL_PROJECT, AUDIT_PROJECT FROM
(SELECT COUNT(1) AS ALL_PROJECT FROM project A LEFT JOIN project_person P ON A.pkid = P.project_id
A.project_status != 0
AND P.area_code = #{areaCode}
AND P.town_code = #{townCode}
)A,
(SELECT COUNT(1) AS AUDIT_PROJECT FROM project B LEFT JOIN project_person P ON B.pkid = P.project_id
B.project_status IN (6, 7, 8)
AND P.area_code = #{areaCode}
AND P.town_code = #{townCode}
)B

select date_format(A.create_date,'%Y-%m-%d') AS TDATE, cast(A.project_kind as char) AS KIND, cast( SUM( ifnull(B.show_preice,0) ) / 10000 AS char ) AS TOTAL FROM project A, project_around B
A.pkid = B.project_id AND A.project_status = 4
AND DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(A.create_date)
AND date(A.create_date) >= #{startDate}
AND date(A.create_date) <= #{endDate}
GROUP BY TDATE,KIND

SELECT COUNT(1) FROM project p left JOIN project_person pp ON p.pkid = pp.project_id
p.project_status != 0
AND pp.town_code = #{areaCode} OR pp.area_code = #{areaCode}

SELECT project_kind AS projectKind, count(1) AS projectNum FROM project P
P.project_status = 4
AND DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(P.create_date)
AND date(P.create_date) >= #{startDate}
AND date(P.create_date) <= #{endDate}
GROUP BY P.project_kind
